import { createSlice } from "@reduxjs/toolkit";
import { logIn, logOut, signUp, follow, unfollow, changeNickname } from "../thunks/user";

const userSlice = createSlice({
  name: 'user',
  initialState: {
    logInLoading: false,
    logInDone: false,
    logInError: null,
    logOutLoading: false,
    logOutDone: false,
    logOutError: null,
    signUpLoading: false,
    signUpDone: false,
    signUpError: null,
    followLoading: false,
    followDone: false,
    followError: null,
    unfollowLoading: false,
    unfollowDone: false,
    unfollowError: null,
    changeNicknameLoading: false,
    changeNicknameDone: false,
    changeNicknameError: null,
    navigateProfile: 0,
    my: null,
    User: []
  },
  reducers: { addPostToMe,
    addPostToMe(state, action) {
      state.my.Posts.unshift({ id: action.payload });
    },
    removePostToMe(state, action) {
      state.my.Posts = state.my.Posts.filter((post) => post.id !== action.payload);
    },
    navigateProfile(state, action){
      state.navigateProfile = action.payload
    },
  },
  extraReducers: {
   [logIn.pending]: (state, action)=>{
      state.logInLoading = true;
      state.logInDone = false;
      state.logInError = null;
    },
   [logIn.fulfilled]: (state, action)=>{
      state.logInLoading = false;
      state.my = action.payload;
      state.logInDone = true;
    },
   [logIn.rejected]: (state, action)=>{
      state.logInLoading = false;
      state.my = action.payload;
      state.logInDone = true;
    },
   [logOut.pending]: (state, action)=>{
      state.logOutLoading = true;
      state.logOutDone = false;
      state.logOutError = null;
    },
   [logOut.fulfilled]: (state, action)=>{
      state.my = null;
      state.logOutLoading = false;
      state.logOutDone = true;
    },
   [logOut.rejected]: (state, action)=>{
      state.logOutLoading = false;
      state.logOutError = action.error.message;
    },
   [signUp.pending]: (state, action)=>{
      state.signUpLoading = true;
      state.signUpDone = false;
      state.signUpError = null;
    },
   [signUp.fulfilled]: (state, action)=>{
      state.signUpLoading = false;
      state.signUpDone = true;
    },
   [signUp.rejected]: (state, action)=>{
      state.signUpLoading = false;
      state.signUpError = action.error.message;
    },
   [follow.pending]: (state, action)=>{
      state.followLoading = true;
      state.followDone = false;
      state.followError = null;
    },
   [follow.fulfilled]: (state, action)=>{
      state.my.Followings.unshift({ id: action.payload.id, username: action.payload.username });
      state.followLoading = false;
      state.followDone = true;
    },
   [follow.rejected]: (state, action)=>{
      state.followLoading = false;
      state.followError = action.error.message;
    },
   [unfollow.pending]: (state, action)=>{
      state.unfollowLoading = true;
      state.unfollowDone = false;
      state.unfollowError = null;
    },
   [unfollow.fulfilled]: (state, action)=>{
      state.my.Followings = state.my.Followings.filter((user) => user.id !== action.payload);
      state.unfollowLoading = false;
      state.unfollowDone = true;
    },
   [unfollow.rejected]: (state, action)=>{
      state.unfollowLoading = false;
      state.unfollowError = action.error.message;
    },
   [changeNickname.pending]: (state, action)=>{
      state.changeNicknameLoading = true;
      state.changeNicknameDone = false;
      state.changeNicknameError = null;
    },
   [changeNickname.fulfilled]: (state, action)=>{
      state.changeNicknameLoading = false;
      state.changeNicknameDone = true;
    },
   [changeNickname.rejected]: (state, action)=>{
      state.changeNicknameLoading = false;
      state.changeNicknameError = action.error.message;
    },
  }
})

export {  }
export default userSlice;
