import {
    LoadPostsActionType,
    AddPostActionType,
    RemovePostActionType,
    AddCommentActionType,
    RemoveCommentActionType,
    LikePostActionType,
    UnlikePostActionType
} from "../action-types/post";
import {
    AddCommentRequestAction,
    AddPostFailureAction,
    AddPostRequestAction,
    AddPostSuccessAction,
    AddPostToMeAction,
    LikePostRequestAction,
    LoadPostsFailureAction,
    LoadPostsRequestAction,
    LoadPostsSuccessAction,
    RemoveCommentRequestAction,
    RemovePostFailureAction,
    RemovePostOfMeActionAction,
    RemovePostRequestAction,
    RemovePostSuccessAction,
    UnlikePostRequestAction
} from "../actions/post";
import { User } from '../state-types/user';
import { Post, Comment, Like } from '../state-types/post';
import { getDummyPosts, postMyDummyPost, postMyDummyComment } from '../reducers/post'
import {AddPostToMeActionType, RemovePostOfMeActionType} from "../action-types/user";
export const loadPostsRequest: LoadPostsRequestAction = {
  type: LoadPostsActionType.LOAD_POSTS_REQUEST,
}

export const loadPostsSuccess = (number: number): LoadPostsSuccessAction => {
  return {
    type: LoadPostsActionType.LOAD_POSTS_SUCCESS,
    data: getDummyPosts(number)
  }
}

export const loadPostsFailure = (error: string) : LoadPostsFailureAction => {
  return {
    type: LoadPostsActionType.LOAD_POSTS_FAILURE,
    error
  }
}

export const addPostRequest = (data: {
  content: string,
  userId: number | string,
  User: Partial<User>
}): AddPostRequestAction => {
  return {
    type: AddPostActionType.ADD_POST_REQUEST,
    data
  }
}

// before connect with backend
export const addPostSuccess = (data: Post, id: string): AddPostSuccessAction => {
  return {
    type: AddPostActionType.ADD_POST_SUCCESS,
    data: postMyDummyPost(data, id)
  }
}

export const addPostToMe = (id: string): AddPostToMeAction => {
  return {
    type: AddPostToMeActionType.ADD_POST_TO_ME,
    data: id
  }
}

export const addPostFailure = (error: string): AddPostFailureAction => {
  return {
    type: AddPostActionType.ADD_POST_FAILURE,
    error
  }
}

export const removePostRequest = (postId: number | string): RemovePostRequestAction => {
  return {
    type: RemovePostActionType.REMOVE_POST_REQUEST,
    data: postId
  }
}

export const removePostSuccess = (postId: number | string): RemovePostSuccessAction => {
  return {
    type: RemovePostActionType.REMOVE_POST_SUCCESS,
    data: postId
  }
}

export const removePostOfMe = (postId: number | string): RemovePostOfMeActionAction => {
  return {
    type: RemovePostOfMeActionType.REMOVE_POST_OF_ME,
    data: postId
  }
}

export const removePostFailure = (error: string): RemovePostFailureAction => {
  return {
    type: RemovePostActionType.REMOVE_POST_FAILURE,
    error
  }
}
export const addCommentRequest = (data: {
  content: string,
  postId: number | string,
  userId: number | string,
  User: Partial<User>
}): AddCommentRequestAction => {
  return {
    type: AddCommentActionType.ADD_COMMENT_REQUEST,
    data
  }
}

// 백엔드 만들면 이런 식으로 data 안 옴. 백엔드 만드는 게 더 나을듯. ssr이기도 해서 next쓰려면 백
export const addCommentSuccess = (data: {
    content: string,
    postId: number | string,
    userId: number | string,
    User: Partial<User>
}): AddCommentRequestAction => {
    return {
        type: AddCommentActionType.ADD_COMMENT_REQUEST,
        data
    }
}

export const removeCommentRequest = (comment: Partial<Comment>): RemoveCommentRequestAction => {
  return {
    type: RemoveCommentActionType.REMOVE_COMMENT_REQUEST,
    data: comment
  }
}


export const likePostRequest = (data: {
    postId: number | string,
    userId: number | string,
}): LikePostRequestAction => {
  return {
    type: LikePostActionType.LIKE_POST_REQUEST,
    data
  }
}

export const unlikePostRequest = (data: {
    postId: number | string,
    userId: number | string,
}): UnlikePostRequestAction => {
  return {
    type: UnlikePostActionType.UNLIKE_POST_REQUEST,
    data
  }
}
