import { useSelector, useDispatch } from "react-redux";
import { retweet, undoRetweet } from "../store/thunks/post";
import React, { BaseSyntheticEvent, Dispatch } from "react";
import RootState from "../store/state-types";
import { Post } from '../store/state-types/post';

import { StyledMenu } from "../styles";
import { MenuItem } from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import ListItemDecorator from "@mui/joy/ListItemDecorator";
import DeleteIcon from "@mui/icons-material/Delete";
import PersonAddAltRoundedIcon from "@mui/icons-material/PersonAddAltRounded";
import BackspaceIcon from '@mui/icons-material/Backspace';
import FlagIcon from "@mui/icons-material/Flag";
import { LoadingButton } from "@mui/lab";

interface RetweetMenuProps {
  post: Post;
  anchorEl: BaseSyntheticEvent["currentTarget"];
  setAnchorEl: Dispatch<any>;
  open: boolean;
  handleRetweetButton: any;
}

const RetweetMenu: React.FC<RetweetMenuProps> = ({ post, anchorEl, setAnchorEl, open, handleRetweetButton }) => {
  const { retweetLoading, undoRetweetLoading } = useSelector((state: RootState) => state.post);
  const dispatch = useDispatch();
  const handleClose = () => {
    setAnchorEl(null);
  };

  const isRetweeted = post.RetweetId;

  const handleRetweet = (post: Post) => {
    if(isRetweeted){
      dispatch(undoRetweet(post));
    } else {
      dispatch(retweet(post));
    }
  }

  return (
      <StyledMenu
          id="demo-customized-menu"
          MenuListProps={{
            'aria-labelledby': 'demo-customized-button',
          }}
          anchorEl={anchorEl}
          open={open}
          onClose={handleClose}
      >
          <MenuItem key={isRetweeted ?"UndoRetweet" : "Retweet"} onClick={()=>handleFollowing(item)} disableRipple>
            <LoadingButton loading={isRetweeted ? undoRetweetLoading : retweetLoading} sx={{ p: 0, color: "inherit", textTransform: "inherit", fontSize: "inherit" }}>
              <ListItemDecorator sx={{color: 'inherit'}}>
                <PersonAddAltRoundedIcon/>
              </ListItemDecorator>{' '}
              {isRetweeted ? "UndoRetweet" : "Retweet"}
            </LoadingButton>
          </MenuItem>
          <MenuItem key="Quote Tweet" onClick={handleClose} disableRipple>
            <FlagIcon/>
            Quote Tweet
          </MenuItem>
      </StyledMenu>
  )
}

export default RetweetMenu;
