            }));
          }
          setPrevLastId(mainPosts[mainPosts.length - 1]?.id);
        }
      }
    }

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    }
  }, [loadPostsLoading, bringMorePosts])

  return (
      <AppLayout>
        <Head>
          <title>
            {singlePost.User.username}
            님의 글
          </title>
          <meta name="description" content={singlePost.content} />
          <meta property="og:title" content={`${singlePost.User.username}님의 게시글`} />
          <meta property="og:description" content={singlePost.content} />
          <meta property="og:image" content={singlePost.Images[0] ? singlePost.Images[0].src : 'https://nodebird.com/favicon.ico'} />
          <meta property="og:url" content={`https://nodebird.com/post/${id}`} />
        </Head>
        <PostCard post={singlePost} />
      </AppLayout>
  )
}

export const getServerSideProps = wrapper.getServerSideProps(async (context)=>{
  const cookie = context.req ? context.req.headers.cookie : '';
  axios.defaults.headers.Cookie = '';
  if (cookie) {
    axios.defaults.headers.Cookie = cookie;
  }
  await context.store.dispatch(loadMyInfo());
  await context.store.dispatch(loadSingleUser());
  await context.store.dispatch(loadPostsByUser({ id: context.params.id }));

  console.log(context.req);
})

export default Post;
