import { useSelector, useDispatch } from "react-redux";
import { followRequestAction } from "../reducers/user";
import React, { useState } from "react";

import { StyledMenu } from "../styles";
import {
  Avatar,
  Divider,
  IconButton,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Menu,
  MenuItem,
  Tooltip
} from "@mui/material";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import EditIcon from "@mui/icons-material/Edit";
import ListItemDecorator from "@mui/joy/ListItemDecorator";
import DeleteIcon from "@mui/icons-material/Delete";
import PersonAddAltRoundedIcon from "@mui/icons-material/PersonAddAltRounded";
import FlagIcon from "@mui/icons-material/Flag";
import {LoadingButton} from "@mui/lab";

const MoreMenu = ({ item, anchorEl, setAnchorEl, open, handleDelete, removeLoading }) => {
  const { my, followLoading } = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleFollow = (item) => {
    dispatch(followRequestAction(item.User.id));
  }

  return (
      <StyledMenu
          id="demo-customized-menu"
          MenuListProps={{
            'aria-labelledby': 'demo-customized-button',
          }}
          anchorEl={anchorEl}
          open={open}
          onClose={handleClose}
      >
        {my?.id == item.User.id ? <>
          <MenuItem key="Edit" onClick={handleClose} disableRipple>
            <EditIcon/>
            Edit
          </MenuItem>
          <MenuItem key="Delete" onClick={handleDelete} variant="soft" color="danger">
            <LoadingButton loading={removeLoading} sx={{ p: 0, color: "inherit", textTransform: "inherit", fontSize: "inherit"  }}>
              <ListItemDecorator sx={{color: 'inherit'}}>
                <DeleteIcon/>
              </ListItemDecorator>{' '}
              Delete
            </LoadingButton>
          </MenuItem>
        </> : <>
          {my.Followings.find((user)=> user.id == item.User.id) 
              ?
              <MenuItem key="Follow" onClick={(item)=>handleFollow(item)} disableRipple>
                <LoadingButton loading={followLoading} sx={{ p: 0, color: "inherit", textTransform: "inherit", fontSize: "inherit" }}>
                  <ListItemDecorator sx={{color: 'inherit'}}>
                    <PersonAddAltRoundedIcon/>
                  </ListItemDecorator>{' '}
                  Follow
                </LoadingButton>
              </MenuItem>
              :
              <MenuItem key="Unfollow" onClick={(item)=>handleUnfollow(item)} disableRipple>
                <LoadingButton loading={followLoading} sx={{ p: 0, color: "inherit", textTransform: "inherit", fontSize: "inherit" }}>
                  <ListItemDecorator sx={{color: 'inherit'}}>
                    <PersonAddAltRoundedIcon/>
                  </ListItemDecorator>{' '}
                  Unfollow
                </LoadingButton>
              </MenuItem>
          }
          <MenuItem key="Follow" onClick={(item)=>handleFollow(item)} disableRipple>
            <LoadingButton loading={followLoading} sx={{ p: 0, color: "inherit", textTransform: "inherit", fontSize: "inherit" }}>
              <ListItemDecorator sx={{color: 'inherit'}}>
                <PersonAddAltRoundedIcon/>
              </ListItemDecorator>{' '}
              Follow
            </LoadingButton>
          </MenuItem>
          <MenuItem key="Report" onClick={handleClose} disableRipple>
            <FlagIcon/>
            Report
          </MenuItem>
        </>}
      </StyledMenu>
  )
}

export default MoreMenu;
