import { useState } from "react";

import {
  Avatar,
  Divider,
  IconButton,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Menu,
  MenuItem,
  Tooltip
} from "@mui/material";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import EditIcon from "@mui/icons-material/Edit";
import ListItemDecorator from "@mui/joy/ListItemDecorator";
import DeleteIcon from "@mui/icons-material/Delete";
import PersonAddAltRoundedIcon from "@mui/icons-material/PersonAddAltRounded";
import FlagIcon from "@mui/icons-material/Flag";
import {alpha, styled} from "@mui/material/styles";
import MoreMenu from "./MoreMenu";

const CommentListItem = ({ comment }) => {
  const [expanded, setExpanded] = useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const [tweetAnchorEl, setTweetAnchorEl] = React.useState(null);
  const [replyAnchorEl, setReplyAnchorEl] = React.useState(null);

  const replyOpen = Boolean(replyAnchorEl);

  const handleTweetClose = () => {
    setTweetAnchorEl(null);
  };

  const handleReplyClick = (event) => {
    setReplyAnchorEl(event.currentTarget);
  };


  const handleReplyClose = () => {
    setReplyAnchorEl(null);
  };

  return (
      <>
        <ListItem key={comment.id}
                  secondaryAction={
                    <>
                      <Tooltip title="More">
                        <IconButton
                            aria-label="more"
                            id="long-button"
                            aria-controls={replyOpen ? 'long-menu' : undefined}
                            aria-expanded={replyOpen ? 'true' : undefined}
                            aria-haspopup="true"
                            onClick={handleReplyClick}
                        >
                          <MoreVertIcon />
                        </IconButton>
                      </Tooltip>
                      <MoreMenu />
                    </>
                  }
                  alignItems="flex-start">
          <ListItemAvatar>
            <Avatar alt={comment.User.username} src="/static/images/avatar/1.jpg"/>
          </ListItemAvatar>
          <ListItemText
              sx={{ m: 1 }}
              primary={comment.User.username}
              secondary={
                <React.Fragment>
                  {comment.content}
                </React.Fragment>
              }
          />
        </ListItem>
        <Divider variant="inset" component="li" />
      </>
  )
}

const StyledMenu = styled((props) => (
    <Menu
        elevation={0}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'right',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        {...props}
    />
))(({ theme }) => ({
  '& .MuiPaper-root': {
    borderRadius: 6,
    marginTop: theme.spacing(1),
    minWidth: 180,
    color:
        theme.palette.mode === 'light' ? 'rgb(55, 65, 81)' : theme.palette.grey[300],
    boxShadow:
        'rgb(255, 255, 255) 0px 0px 0px 0px, rgba(0, 0, 0, 0.05) 0px 0px 0px 1px, rgba(0, 0, 0, 0.1) 0px 10px 15px -3px, rgba(0, 0, 0, 0.05) 0px 4px 6px -2px',
    '& .MuiMenu-list': {
      padding: '4px 0',
    },
    '& .MuiMenuItem-root': {
      '& .MuiSvgIcon-root': {
        fontSize: 18,
        color: theme.palette.text.secondary,
        marginRight: theme.spacing(1.5),
      },
      '&:active': {
        backgroundColor: alpha(
            theme.palette.primary.main,
            theme.palette.action.selectedOpacity,
        ),
      },
    },
  },
}));

export default CommentListItem;
