  imageUpload: false,
  addPost: false,
}

const ADD_POST = 'ADD_POST';
const addPost = (data) => {
  return {
    type: ADD_POST,
    data
  }
}

const reducer = (state = initialState, action) => {
  switch (action.type){
    case ADD_POST:
      return {
        mainPosts: [
          action.data,
          ...state.mainPosts
        ],
        addPost: true,
        ...state
      }
    default:
      return state;
  }
}

export default reducer;
