export const initialState = {
  isLoggedIn: false,
  my: null,
  User: [
    {
      id: 1,
      username: "Shrimp and Chorizo Paella",
      email: "shrimp@gmail.com",
      pass: "test1",
      Followings: null,
      Followers: null,
      Posts: null
    },
    {
      id: 2,
      username: "Mary S Bing",
      email: "msbing@gmail.com",
      pass: "test2",
      Followings: null,
      Followers: null,
      Posts: null
    },
    {
      id: 3,
      username: "Rose A Kramer",
      email: "rose@gmail.com",
      pass: "test3",
      Followings: null,
      Followers: null,
      Posts: null
    },
    {
      id: 4,
      username: "Joy T Jones",
      email: "joy@gmail.com",
      pass: "test4",
      Followings: null,
      Followers: null,
      Posts: null
    },
  ]
}

export const loginRequestAction = (data) => {
  return {
    type: 'LOG_IN',
    data
  }
}

export const logoutRequestAction = {
  type: 'LOG_OUT',
}

const reducer = (state = initialState, action) => {
  switch (action.type){
    case 'LOG_IN':
      return {
          ...state,
          isLoggedIn: true,
          my: action.data
        }
    case 'LOG_OUT':
      return {
          ...state,
          isLoggedIn: false,
          my: null
        }
    default:
      return state;
  }
}


export default reducer;
