import { useSelector, useDispatch } from 'react-redux';
import { loadPostsRequest } from "../reducers/post";
import { useEffect } from "react";

import AppLayout from '../components/AppLayout';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
const Home = () => {
  const { my } = useSelector((state)=>state.user);
  const mainPosts = useSelector((state)=>state.post.mainPosts);
  const dispatch = useDispatch();

  useEffect(()=>{
    dispatch(loadPostsRequest());
  }, [])

  useEffect(()=>{
    function handleScroll() {

    }
    window.addEventListener("scroll", () => {
      console.log(document.documentElement.clientHeight, document.documentElement.scrollHeight)
    });

    return () => {
      window.removeEventListener("scroll", () => {
        console.log(document.documentElement.clientHeight, document.documentElement.scrollHeight)
      });
    }
  }, [])

  return (
    <>
      <AppLayout>
        {my && <PostForm/>}
        {mainPosts.map((post)=>(<PostCard post={post} key={post.id} />))}
      </AppLayout>
    </>
  )
}

export default Home;
