import { useState } from 'react';
import ListSubheader from '@mui/material/ListSubheader';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';

const FollowList = ({ role }) => {
  const [checked, setChecked] = useState([1]);
  const [text, setText] = useState("Following");
  const [index, setIndex] = useState(-1);

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
    <List 
      dense 
      sx={{ width: '100%', bgcolor: 'background.paper' }}
      subheader={
        <ListSubheader component="div" id="nested-list-subheader">
          {role}
        </ListSubheader>
      }
    >
      {[0, 1, 2, 3, 4].map((value, idx) => {
        const labelId = `error`;
        return (
          <ListItem
            key={value}
            secondaryAction={
            <Button 
              variant="outlined" 
              onMouseEnter={()=>{setIndex(idx); setText("Unfollow");}}
              sx={{
                width: '110px',
                "&:hover": {
                  backgroundColor: "rgba(211, 47, 47, 0.04)",
                  border: "1px solid #d32f2f",
                  color: "#d32f2f"
                } 
              }}
            >{idx == index ? text : "Following"}</Button>
            }
            disablePadding
          >
            <ListItemButton>
              <ListItemAvatar>
                <Avatar
                  alt={`Avatar n°${value + 1}`}
                  src={`/static/images/avatar/${value + 1}.jpg`}
                />
              </ListItemAvatar>
              <ListItemText id={labelId} primary={`Lila R. Mabry ${value + 1}`} />
            </ListItemButton>
          </ListItem>
        );
      })}
    </List>
  );
}

export default FollowList;
