import { useState, useEffect, useCallback } from "react";
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { loginRequestAction, dummyMy } from '../reducers/user';
import Link from 'next/link';
import {
  Box,
  IconButton,
  OutlinedInput,
  InputLabel,
  InputAdornment,
  FormGroup,
  FormControl,
  ButtonGroup,
  Button
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { LoadingButton } from '@mui/lab';

const LoginForm = () => {
  const { logInLoading, logIn } = useSelector((state)=>state.user)
  const dispatch = useDispatch();
  const [form, setForm] = useState({
    email: "",
    pass:""
  });
  const { email, pass } = form;

  const [showPassword, setShowPassword] = useState(false);

  useEffect(()=>{}, [])
  const handleClickShowPassword = () => setShowPassword((show) => !show);
  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  }, [form]);


  const handleSubmit = useCallback((e) => {
    e.preventDefault();
    dispatch(loginRequestAction(form));
    setForm({
      email: "",
      pass:""
    })
  }, [form]);

  return (
    <Box sx={{ display: "flex", flexWrap: "wrap" }}>
      <form onSubmit={handleSubmit}>
      <FormControl sx={{ m: 1, width: "100%" }} variant="outlined" style={{ marginBottom: "4px" }}>
          <InputLabel htmlFor="Email">
            Email
          </InputLabel>
          <OutlinedInput
            type="email"
            name="email"
            value={email}
            onChange={handleChange}
            id="Email"
            label="Email"
          />
        </FormControl>

        <FormControl sx={{ m: 1, width: "100%" }} variant="outlined" style={{ marginTop: "4px" }}>
          <InputLabel htmlFor="Password">
            Password
          </InputLabel>
          <OutlinedInput
            type={showPassword ? "text" : "password"}
            name="pass"
            value={pass}
            onChange={handleChange}
            id="Password"
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Password"
          />
        </FormControl>
        <ButtonGroup aria-label="outlined button group" style={{ width: '100%', margin: '0 8px' }}>
          <LoadingButton type="submit" variant="contained" loading={logInLoading}>Login</LoadingButton>
          <Button variant="outlined">
            <Link href="/signup">
              <a style={{ textDecoration: 'none', color: '#1976d2' }}>
                Sign Up
              </a>
            </Link>
          </Button>
        </ButtonGroup>
      </form>
    </Box>
  );
};

export default LoginForm;
