import {Avatar, Divider, IconButton, ListItem, ListItemAvatar, ListItemText, MenuItem, Tooltip} from "@mui/material";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import EditIcon from "@mui/icons-material/Edit";
import ListItemDecorator from "@mui/joy/ListItemDecorator";
import DeleteIcon from "@mui/icons-material/Delete";
import PersonAddAltRoundedIcon from "@mui/icons-material/PersonAddAltRounded";
import FlagIcon from "@mui/icons-material/Flag";
import React from "react";

const CommentListItem = ({ comment }) => {
  return (
      <>
        <ListItem key={comment.id}
                  secondaryAction={
                    <>
                      <Tooltip title="More">
                        <IconButton
                            aria-label="more"
                            id="long-button"
                            aria-controls={replyOpen ? 'long-menu' : undefined}
                            aria-expanded={replyOpen ? 'true' : undefined}
                            aria-haspopup="true"
                            onClick={handleReplyClick}
                        >
                          <MoreVertIcon />
                        </IconButton>
                      </Tooltip>
                      <StyledMenu
                          id="demo-customized-menu"
                          MenuListProps={{
                            'aria-labelledby': 'demo-customized-button',
                          }}
                          anchorEl={replyAnchorEl}
                          open={replyOpen}
                          onClose={handleReplyClose}
                      >
                        <MenuItem onClick={handleReplyClose} disableRipple>
                          <EditIcon />
                          Edit
                        </MenuItem>
                        <MenuItem onClick={handleReplyClose} variant="soft" color="danger">
                          <ListItemDecorator sx={{ color: 'inherit' }}>
                            <DeleteIcon />
                          </ListItemDecorator>{' '}
                          Delete
                        </MenuItem>
                        <MenuItem onClick={handleTweetClose} disableRipple>
                          <PersonAddAltRoundedIcon />
                          Follow
                        </MenuItem>
                        <MenuItem onClick={handleReplyClose} disableRipple>
                          <FlagIcon />
                          Report
                        </MenuItem>
                      </StyledMenu>
                    </>
                  }
                  alignItems="flex-start">
          <ListItemAvatar>
            <Avatar alt={comment.User.username} src="/static/images/avatar/1.jpg"/>
          </ListItemAvatar>
          <ListItemText
              sx={{ m: 1 }}
              primary={comment.User.username}
              secondary={
                <React.Fragment>
                  {comment.content}
                </React.Fragment>
              }
          />
        </ListItem>
        <Divider variant="inset" component="li" />
      </>
  )
}

export default CommentListItem;
