export const logoutRequestAction = {
  type: 'LOG_OUT',
}

const reducer = (state = initialState, action) => {
  switch (action.type){
    case 'LOG_IN':
      return {
          ...state,
          isLoggedIn: true,
          my: action.data
        }
    case 'LOG_OUT':
      return {
          ...state,
          isLoggedIn: false,
          my: null
        }
    default:
      return state;
  }
}

