const initialState = {
  user: {
    isLoggediIn: false,
    user: null
  },
  post: {

  }
}

export const loginAction = (data) => {
  return {
    type: 'LOG_IN',
    data
  }
}

export const logoutAction = () => {
  return {
    type: 'LOG_OUT',
  }
}

const rootReducer = (state = initialState, action) => {
  switch(action.type){
    case 'LOG_IN':
      return {
        ...state, 
        user: {
          ...state,
          isLoggedIn: true,
          user: action.data
        }
      }
      break; 
    case 'LOG_OUT':
      return {
        ...state,
        user: {
          ...state,
          isLoggedIn: false,
          user: null
        }
      }
    break;
    default:
  }
}

export default rootReducer;
