  return (
      <>
        <ListItem key={comment.id}
          secondaryAction={
            <>
              <Tooltip title="More">
                <IconButton
                  aria-label="more"
                  id="long-button"
                  aria-controls={open ? 'long-menu' : undefined}
                  aria-expanded={open ? 'true' : undefined}
                  aria-haspopup="true"
                  onClick={handleReplyClick}
                >
                  <MoreVertIcon/>
                </IconButton>
              </Tooltip>
              <StyledMenu
                  id="demo-customized-menu"
                  MenuListProps={{
                    'aria-labelledby': 'demo-customized-button',
                  }}
                  anchorEl={tweetAnchorEl}
                  open={tweetOpen}
                  onClose={handleTweetClose}
              >
                <MenuItem key={0} onClick={handleTweetClose} disableRipple>
                  <EditIcon/>
                  Edit
                </MenuItem>
                <MenuItem key={1} onClick={handleReplyClose} variant="soft" color="danger">
                  <ListItemDecorator sx={{color: 'inherit'}}>
                    <DeleteIcon/>
                  </ListItemDecorator>{' '}
                  Delete
                </MenuItem>
                <MenuItem key={0} onClick={handleTweetClose} disableRipple>
                  <PersonAddAltRoundedIcon/>
                  Follow
                </MenuItem>
                <MenuItem key={1} onClick={handleTweetClose} disableRipple>
                  <FlagIcon/>
                  Report
                </MenuItem>
              </StyledMenu>
            </>
        } alignItems="flex-start">
          <ListItemAvatar>
            <Avatar alt={comment.User.username} src="/static/images/avatar/1.jpg"/>
          </ListItemAvatar>
          <ListItemText
              sx={{m: 1}}
              primary={comment.User.username}
              secondary={comment.content}
          />
        </ListItem>
        <Divider variant="inset" component="li"/>
      </>
  )
}

export default CommentListItem;
