import React, { useState } from 'react';
import { useSelector } from 'react-redux';

import CommentForm from '../components/CommentForm';

import { styled } from '@mui/material/styles';
import {Card,CardHeader,CardMedia,CardContent,CardActions,Collapse,Avatar,IconButton,Typography,List,ListItem,Divider,ListItemText,ListItemAvatar,ListSubheader, Menu, MenuItem, Tooltip} from '@mui/material';
import { red, blue } from '@mui/material/colors';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import RepeatIcon from '@mui/icons-material/Repeat';
import ModeCommentOutlinedIcon from '@mui/icons-material/ModeCommentOutlined';
import { alpha } from '@mui/material/styles';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import FlagIcon from '@mui/icons-material/Flag';
import PersonAddAltRoundedIcon from '@mui/icons-material/PersonAddAltRounded';
import ListItemDecorator from '@mui/joy/ListItemDecorator';

const PostCard = ({ post }) => {
  const [expanded, setExpanded] = useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const [tweetAnchorEl, setTweetAnchorEl] = React.useState(null);
  const [replyAnchorEl, setReplyAnchorEl] = React.useState(null);

  const tweetOpen = Boolean(tweetAnchorEl);
  const replyOpen = Boolean(replyAnchorEl);

  const handleTweetClick = (event) => {
    setTweetAnchorEl(event.currentTarget);
  };

  const handleTweetClose = () => {
    setTweetAnchorEl(null);
  };

  const handleReplyClick = (event) => {
    setReplyAnchorEl(event.currentTarget);
  };


  const handleReplyClose = () => {
    setReplyAnchorEl(null);
  };

  return (
      <Card sx={{ m: 1 }}>
        <CardHeader
            avatar={
              <Avatar sx={{ bgcolor: blue[500] }} aria-label="recipe">
                R
              </Avatar>
            }
            action={
              <>
                <Tooltip title="More">
                <IconButton
                    aria-label="more"
                    id="long-button"
                    aria-controls={tweetOpen ? 'long-menu' : undefined}
                    aria-expanded={tweetOpen ? 'true' : undefined}
                    aria-haspopup="true"
                    onClick={handleTweetClick}
                >
                  <MoreVertIcon />
                </IconButton>
                </Tooltip>
                <StyledMenu
                    id="demo-customized-menu"
                    MenuListProps={{
                      'aria-labelledby': 'demo-customized-button',
                    }}
                    anchorEl={tweetAnchorEl}
                    open={tweetOpen}
                    onClose={handleTweetClose}
                >
                  <MenuItem onClick={handleTweetClose} disableRipple>
                    <EditIcon />
                    Edit
                  </MenuItem>
                  <MenuItem onClick={handleReplyClose} variant="soft" color="danger">
                    <ListItemDecorator sx={{ color: 'inherit' }}>
                      <DeleteIcon />
                    </ListItemDecorator>{' '}
                    Delete
                  </MenuItem>
                  <MenuItem onClick={handleTweetClose} disableRipple>
                    <PersonAddAltRoundedIcon />
                    Follow
                  </MenuItem>
                  <MenuItem onClick={handleTweetClose} disableRipple>
                    <FlagIcon />
                    Report
                  </MenuItem>
                </StyledMenu>
              </>

            }
            title={post.User.username}
            subheader={post.createdAt}
        />
        <CardMedia
            component="img"
            height="100%"
            image="https://upload.wikimedia.org/wikipedia/commons/e/ec/Shoyu_ramen%2C_at_Kasukabe_Station_%282014.05.05%29_1.jpg"
            alt="Paella dish"
        />
        <CardContent>
          <Typography variant="body2" color="text.secondary">
            This impressive paella is a perfect party dish and a fun meal to cook
            together with your guests. Add 1 cup of frozen peas along with the mussels,
            if you like.
          </Typography>
        </CardContent>
        <CardActions disableSpacing>
          <Tooltip title="Like">
          <IconButton aria-label="add to favorites">
            <FavoriteBorderIcon />
          </IconButton>
          </Tooltip>
          <Tooltip title="Retweet">
          <IconButton aria-label="retweet">
            <RepeatIcon />
          </IconButton>
          </Tooltip>
          <Tooltip title="Reply">
          <ExpandMore
              expand={expanded}
              onClick={handleExpandClick}
              aria-expanded={expanded}
              aria-label="show more"
          >
            <ModeCommentOutlinedIcon />
          </ExpandMore>
          </Tooltip>
        </CardActions>
        <Collapse in={expanded} timeout="auto" unmountOnExit>
          <CommentForm />
          <List
              subheader={<ListSubheader>{commentData.length}개의 댓글</ListSubheader>}
              sx={{ m: 1, bgcolor: 'background.paper' }}
          >
            {commentData.map((comment)=>(
                <>
              <ListItem
                  secondaryAction={
                    <>
                      <Tooltip title="More">
                      <IconButton
                          aria-label="more"
                          id="long-button"
                          aria-controls={replyOpen ? 'long-menu' : undefined}
                          aria-expanded={replyOpen ? 'true' : undefined}
                          aria-haspopup="true"
                          onClick={handleReplyClick}
                      >
                        <MoreVertIcon />
                      </IconButton>
                      </Tooltip>
                      <StyledMenu
                          id="demo-customized-menu"
                          MenuListProps={{
                            'aria-labelledby': 'demo-customized-button',
                          }}
                          anchorEl={replyAnchorEl}
                          open={replyOpen}
                          onClose={handleReplyClose}
                      >
                        <MenuItem onClick={handleReplyClose} disableRipple>
                          <EditIcon />
                          Edit
                        </MenuItem>
                        <MenuItem onClick={handleReplyClose} variant="soft" color="danger">
                          <ListItemDecorator sx={{ color: 'inherit' }}>
                            <DeleteIcon />
                          </ListItemDecorator>{' '}
                          Delete
                        </MenuItem>
                        <MenuItem onClick={handleTweetClose} disableRipple>
                          <PersonAddAltRoundedIcon />
                          Follow
                        </MenuItem>
                        <MenuItem onClick={handleReplyClose} disableRipple>
                          <FlagIcon />
                          Report
                        </MenuItem>
                      </StyledMenu>
                    </>
                  }
                  alignItems="flex-start">
              <ListItemAvatar>
                <Avatar alt={comment.username} src="/static/images/avatar/1.jpg"/>
              </ListItemAvatar>
              <ListItemText
                  sx={{ m: 1 }}
                  primary={comment.username}
                  secondary={
                    <React.Fragment>
                      {comment.comment}
                    </React.Fragment>
                  }
              />
            </ListItem>
              <Divider variant="inset" component="li" /></>))}
          </List>
        </Collapse>
      </Card>
  );
}

